// src/components/TeacMetrics.tsx

import React, { useEffect, useState } from 'react';
import {
  Box, Typography, Grid, Paper, FormControl, InputLabel, Select, MenuItem, SelectChangeEvent
} from '@mui/material';
import { Line, Bar } from 'react-chartjs-2';
import axios from 'axios';
import {
  Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement,
  BarElement, Title, Tooltip, Legend, Filler
} from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, BarElement, Title, Tooltip, Legend, Filler);

const TeacMetrics: React.FC = () => {
  const [userStats, setUserStats] = useState<any>(null);
  const [granularity, setGranularity] = useState<'daily' | 'weekly' | 'monthly'>('monthly');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/metrics/${granularity}`);
        setUserStats(response.data);
      } catch (error) {
        console.error('Error al obtener datos', error);
        setUserStats({ error: 'Error al cargar los datos' });
      }
    };

    fetchData();
  }, [granularity]);

  const handleGranularityChange = (event: SelectChangeEvent<'daily' | 'weekly' | 'monthly'>) => {
    setGranularity(event.target.value as 'daily' | 'weekly' | 'monthly');
  };

  if (userStats && userStats.error) {
    return <Typography variant="h6">{userStats.error}</Typography>;
  }

  if (!userStats) {
    return <Typography variant="h6">Cargando datos...</Typography>;
  }

  const labels = granularity === 'daily' ? userStats.dates || [] 
                : granularity === 'weekly' ? userStats.weeks || []
                : userStats.months || [];
  const values = userStats.values || [];

  const chartData = {
    labels: labels,
    datasets: [
      {
        label: 'Número de Usuarios Nuevos',
        data: values,
        borderColor: granularity === 'monthly' ? '#4CAF50' : '#FF5722',
        backgroundColor: granularity === 'monthly' ? 'rgba(76, 175, 80, 0.2)' : 'rgba(255, 87, 34, 0.2)',
        fill: true,
      }
    ]
  };

  const ChartComponent = granularity === 'daily' ? Line : Bar;

  return (
    <Box sx={{ flexGrow: 1, p: 3 }}>
      <Typography variant="h4" gutterBottom>
        Métricas del Dashboard
      </Typography>
      <FormControl fullWidth sx={{ mb: 3 }}>
        <InputLabel id="granularity-select-label">Granularidad</InputLabel>
        <Select
          labelId="granularity-select-label"
          value={granularity}
          onChange={handleGranularityChange}
        >
          <MenuItem value="daily">Diario</MenuItem>
          <MenuItem value="weekly">Semanal</MenuItem>
          <MenuItem value="monthly">Mensual</MenuItem>
        </Select>
      </FormControl>
      <Grid container spacing={3}>
        <Grid item xs={12} md={12}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom>
              Usuarios Nuevos
            </Typography>
            <ChartComponent data={chartData} />
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default TeacMetrics;
