import React, { useEffect, useState } from 'react';
import { Box, Typography, Grid, Paper, Tabs, Tab } from '@mui/material';
import { Line, Bar, Pie } from 'react-chartjs-2';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
  BarElement,
  ArcElement,
} from 'chart.js';

// Registro de componentes del gráfico
ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, BarElement, ArcElement);

const TeacMetrics: React.FC = () => {
  const [userStats, setUserStats] = useState<any>(null);
  const [granularity, setGranularity] = useState<'daily' | 'monthly' | 'weekly' | 'yearly'>('monthly');
  const [chartType, setChartType] = useState<'line' | 'bar' | 'pie'>('line');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/metrics/${granularity}`);
        setUserStats(response.data);
      } catch (error) {
        console.error('Error al obtener datos', error);
        setUserStats({ error: `Error al cargar los datos ${granularity}` });
      }
    };
    fetchData();
  }, [granularity]);

  const handleGranularityChange = (event: React.SyntheticEvent, newValue: 'daily' | 'monthly' | 'weekly' | 'yearly') => {
    setGranularity(newValue);
  };

  const handleChartTypeChange = (event: React.SyntheticEvent, newValue: 'line' | 'bar' | 'pie') => {
    setChartType(newValue);
  };

  if (userStats && userStats.error) {
    return <Typography variant="h6">{userStats.error}</Typography>;
  }

  if (!userStats || !userStats.dates) {
    return <Typography variant="h6">Cargando datos...</Typography>;
  }

  const labels = granularity === 'daily'
    ? userStats.dates || []
    : granularity === 'weekly'
      ? userStats.weeks || []
      : granularity === 'yearly'
        ? userStats.years || []
        : userStats.months || [];

  const values = userStats.values || [];

  const colors = [
    'rgba(255, 99, 132, 0.6)',
    'rgba(54, 162, 235, 0.6)',
    'rgba(255, 206, 86, 0.6)',
    'rgba(75, 192, 192, 0.6)',
    'rgba(153, 102, 255, 0.6)',
    'rgba(255, 159, 64, 0.6)',
  ];

  const borderColors = [
    'rgba(255, 99, 132, 1)',
    'rgba(54, 162, 235, 1)',
    'rgba(255, 206, 86, 1)',
    'rgba(75, 192, 192, 1)',
    'rgba(153, 102, 255, 1)',
    'rgba(255, 159, 64, 1)',
  ];

  const chartData = {
    labels: labels.length > 0 ? labels : ['Sin datos'],
    datasets: [
      {
        label: 'Número de Usuarios Nuevos',
        data: values.length > 0 ? values : [0],
        borderColor: chartType === 'pie' ? undefined : borderColors[0],
        backgroundColor: chartType === 'pie' ? colors : 'rgba(0, 0, 0, 0)',
        borderWidth: chartType === 'pie' ? 1 : 2,
        fill: chartType === 'line',
      }
    ]
  };

  const lineBarChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: true,
        position: 'top' as const,
      },
      tooltip: {
        callbacks: {
          label: (context: any) => `${context.dataset.label}: ${context.raw}`,
        },
      },
    },
    scales: {
      x: {
        title: {
          display: true,
          text: granularity === 'daily' ? 'Días' : granularity === 'weekly' ? 'Semanas' : granularity === 'yearly' ? 'Años' : 'Meses',
        },
        grid: {
          display: false,
        },
        ticks: {
          autoSkip: true,
          maxTicksLimit: 20,
        },
      },
      y: {
        title: {
          display: true,
          text: 'Número de Usuarios Nuevos',
        },
        grid: {
          borderDash: [5, 5],
        },
        ticks: {
          beginAtZero: true,
        },
      },
    },
  };

  const pieChartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: true,
        position: 'right' as const,
      },
      tooltip: {
        callbacks: {
          label: (context: any) => {
            const label = context.label || '';
            const value = context.raw || '';
            const percentage = ((value / values.reduce((a: number, b: number) => a + b, 0)) * 100).toFixed(2) + '%';
            return `${label}: ${value} (${percentage})`;
          },
        },
      },
    },
  };

  return (
    <Box sx={{ flexGrow: 1, p: 3, height: '100vh', display: 'flex', flexDirection: 'column' }}>
      <Typography variant="h4" gutterBottom>
        Métricas del Dashboard
      </Typography>
      <Box sx={{ flexGrow: 1, overflow: 'hidden', display: 'flex' }}>
        <Box sx={{ flexGrow: 1, p: 3, height: '80%', display: 'flex', flexDirection: 'column' }}>
          <Box sx={{ display: 'flex', justifyContent: 'center', mb: 2 }}>
            <Tabs
              value={granularity}
              onChange={handleGranularityChange}
              indicatorColor="primary"
              textColor="primary"
              centered
              variant="scrollable"
            >
              <Tab label="Diario" value="daily" />
              <Tab label="Semanal" value="weekly" />
              <Tab label="Mensual" value="monthly" />
              <Tab label="Anual" value="yearly" />
            </Tabs>
          </Box>
          <Grid container spacing={3} sx={{ flexGrow: 1, overflow: 'hidden' }}>
            <Grid item xs={12} md={9} sx={{ height: '100%' }}>
              <Paper elevation={3} sx={{ p: 3, height: '100%', display: 'flex', flexDirection: 'column' }}>
                <Typography variant="h6" gutterBottom>
                  {chartType === 'line' ? 'Gráfico de Líneas' : chartType === 'bar' ? 'Gráfico de Barras' : 'Gráfico Circular'}
                </Typography>
                <Box sx={{ flexGrow: 1, position: 'relative' }}>
                  {chartType === 'line' && <Line data={chartData} options={lineBarChartOptions as any} />}
                  {chartType === 'bar' && <Bar data={chartData} options={lineBarChartOptions as any} />}
                  {chartType === 'pie' && <Pie data={chartData} options={pieChartOptions} />}
                </Box>
              </Paper>
            </Grid>
            <Grid item xs={12} md={3} sx={{ display: 'flex', alignItems: 'center', justifyContent: 'center' }}>
              <Tabs
                value={chartType}
                onChange={handleChartTypeChange}
                orientation="vertical"
                indicatorColor="secondary"
                textColor="secondary"
                variant="scrollable"
                sx={{ height: '100%' }}
              >
                <Tab label="Líneas" value="line" />
                <Tab label="Barras" value="bar" />
                <Tab label="Circular" value="pie" />
              </Tabs>
            </Grid>
          </Grid>
        </Box>
      </Box>
    </Box>
  );
};

export default TeacMetrics;
