import React, { useEffect, useState } from 'react';
import { Box, Typography, Grid, Paper, TextField } from '@mui/material';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip as ChartTooltip, Legend, Filler } from 'chart.js';
import { LocalizationProvider, DatePicker, DatePickerProps } from '@mui/x-date-pickers';
import { AdapterDateFns } from '@mui/x-date-pickers/AdapterDateFns';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, ChartTooltip, Legend, Filler);

const TeacMetrics: React.FC = () => {
  const [userStats, setUserStats] = useState<any>(null);
  const [startDate, setStartDate] = useState<Date | null>(null);
  const [endDate, setEndDate] = useState<Date | null>(null);

  useEffect(() => {
    if (startDate && endDate) {
      const fetchData = async () => {
        try {
          const response = await axios.get(`http://localhost:5000/api/users/metrics/daily`, {
            params: {
              start: startDate.toISOString(),
              end: endDate.toISOString()
            }
          });
          setUserStats(response.data);
        } catch (error) {
          console.error('Error al obtener datos', error);
          setUserStats({ error: 'Error al cargar los datos' });
        }
      };

      fetchData();
    }
  }, [startDate, endDate]);

  if (userStats && userStats.error) {
    return <Typography variant="h6" align="center">{userStats.error}</Typography>;
  }

  if (!userStats) {
    return <Typography variant="h6" align="center">Cargando datos...</Typography>;
  }

  const labels = userStats.dates || [];
  const values = userStats.values || [];

  const lineChartData = {
    labels: labels,
    datasets: [
      {
        label: 'Número de Usuarios Nuevos',
        data: values,
        borderColor: '#4CAF50',
        backgroundColor: 'rgba(76, 175, 80, 0.2)',
        fill: true
      }
    ]
  };

  return (
    <Box sx={{ flexGrow: 1, p: 3 }}>
      <Typography variant="h4" gutterBottom align="center">
        Métricas del Dashboard
      </Typography>
      <Box sx={{ mb: 3, display: 'flex', justifyContent: 'center', gap: 2 }}>
        <LocalizationProvider dateAdapter={AdapterDateFns}>
          <DatePicker
            label="Fecha de Inicio"
            value={startDate}
            onChange={(date: Date | null) => setStartDate(date)}
            renderInput={(params: DatePickerProps) => <TextField {...params} fullWidth />}
          />
          <DatePicker
            label="Fecha de Fin"
            value={endDate}
            onChange={(date: Date | null) => setEndDate(date)}
            renderInput={(params: DatePickerProps) => <TextField {...params} fullWidth />}
          />
        </LocalizationProvider>
      </Box>
      <Grid container spacing={3} justifyContent="center" alignItems="center">
        <Grid item xs={12} md={10} lg={8}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom align="center">
              Usuarios Nuevos
            </Typography>
            <Box sx={{ position: 'relative', height: '400px' }}>
              <Line 
                data={lineChartData} 
                options={{
                  responsive: true,
                  maintainAspectRatio: false,
                  scales: {
                    x: {
                      beginAtZero: true,
                    },
                    y: {
                      beginAtZero: true,
                    }
                  }
                }}
              />
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default TeacMetrics;
