import React, { useEffect, useState } from 'react';
import { Box, Typography, Grid, Paper, FormControl, InputLabel, Select, MenuItem, SelectChangeEvent } from '@mui/material';
import { Line } from 'react-chartjs-2';
import axios from 'axios';
import { Chart as ChartJS, CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, PointElement, LineElement, Title, Tooltip, Legend, Filler);

const TeacMetrics: React.FC = () => {
  const [userStats, setUserStats] = useState<any>(null);
  const [granularity, setGranularity] = useState<'daily' | 'monthly'>('monthly');

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/api/users/metrics/${granularity}`);
        setUserStats(response.data);
      } catch (error) {
        console.error('Error al obtener datos', error);
        setUserStats({ error: 'Error al cargar los datos' });
      }
    };

    fetchData();
  }, [granularity]);

  const handleGranularityChange = (event: SelectChangeEvent<'daily' | 'monthly'>) => {
    setGranularity(event.target.value as 'daily' | 'monthly');
  };

  if (userStats && userStats.error) {
    return <Typography variant="h6" align="center">{userStats.error}</Typography>;
  }

  if (!userStats) {
    return <Typography variant="h6" align="center">Cargando datos...</Typography>;
  }

  const labels = granularity === 'daily' ? userStats.dates || [] : userStats.months || [];
  const values = userStats.values || [];

  const lineChartData = {
    labels: labels,
    datasets: [
      {
        label: 'Número de Usuarios Nuevos',
        data: values,
        borderColor: '#4CAF50',
        backgroundColor: 'rgba(76, 175, 80, 0.2)',
        fill: true
      }
    ]
  };

  return (
    <Box sx={{ flexGrow: 1, p: 3 }}>
      <Typography variant="h4" gutterBottom align="center">
        Métricas del Dashboard
      </Typography>
      <FormControl fullWidth sx={{ mb: 3 }}>
        <InputLabel id="granularity-select-label">Granularidad</InputLabel>
        <Select
          labelId="granularity-select-label"
          value={granularity}
          onChange={handleGranularityChange}
        >
          <MenuItem value="daily">Diario</MenuItem>
          <MenuItem value="monthly">Mensual</MenuItem>
        </Select>
      </FormControl>
      <Grid container spacing={3} justifyContent="center" alignItems="center">
        <Grid item xs={12} md={10} lg={8}>
          <Paper elevation={3} sx={{ p: 3 }}>
            <Typography variant="h6" gutterBottom align="center">
              Usuarios Nuevos
            </Typography>
            <Box sx={{ position: 'relative', height: '400px' }}>
              <Line 
                data={lineChartData} 
                options={{
                  responsive: true,
                  maintainAspectRatio: false,
                  scales: {
                    x: {
                      beginAtZero: true,
                    },
                    y: {
                      beginAtZero: true,
                    }
                  }
                }}
              />
            </Box>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default TeacMetrics;
