// src/pages/admin/UserManagementPage.tsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Container, Typography, Grid, Button, Dialog, DialogActions, DialogContent, DialogTitle, TextField, CircularProgress, Alert } from '@mui/material';
import UserList from '../../components/UserList';
import { User } from '../../models/User';

const UserManagementPage: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);
  const [openDialog, setOpenDialog] = useState<boolean>(false);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [formValues, setFormValues] = useState({ username: '', email: '', password: '' });

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Token no encontrado');
      }
      const response = await axios.get('http://localhost:5000/api/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(response.data);
      setError(null);
    } catch (error: any) {
      setError('Error al obtener usuarios: ' + (error.message || error));
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId: string) => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Token no encontrado');
      }
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter(user => user._id !== userId));
      setError(null);
    } catch (error: any) {
      setError('Error al eliminar usuario: ' + (error.message || error));
    } finally {
      setLoading(false);
    }
  };

  const handleOpenDialog = (user: User | null = null) => {
    setSelectedUser(user);
    setFormValues(user ? { username: user.username, email: user.email, password: '' } : { username: '', email: '', password: '' });
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedUser(null);
  };

  const handleFormSubmit = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Token no encontrado');
      }

      if (selectedUser) {
        // Actualizar usuario existente
        await axios.put(`http://localhost:5000/api/users/${selectedUser._id}`, formValues, {
          headers: { Authorization: `Bearer ${token}` },
        });
      } else {
        // Crear nuevo usuario
        await axios.post('http://localhost:5000/api/users', formValues, {
          headers: { Authorization: `Bearer ${token}` },
        });
      }

      fetchUsers();
      handleCloseDialog();
    } catch (error: any) {
      setError('Error al guardar usuario: ' + (error.message || error));
    } finally {
      setLoading(false);
    }
  };

  return (
    <Container>
      <Typography variant="h4" component="h1" gutterBottom>
        Administración de Usuarios
      </Typography>
      {error && <Alert severity="error">{error}</Alert>}
      <Grid container spacing={2} justifyContent="space-between" alignItems="center">
        <Grid item>
          <Button variant="contained" color="primary" onClick={() => handleOpenDialog()}>
            Crear Usuario
          </Button>
        </Grid>
      </Grid>
      {loading ? (
        <CircularProgress />
      ) : (
        <UserList users={users} onDelete={handleDeleteUser} onEdit={handleOpenDialog} />
      )}
      <Dialog open={openDialog} onClose={handleCloseDialog}>
        <DialogTitle>{selectedUser ? 'Editar Usuario' : 'Crear Usuario'}</DialogTitle>
        <DialogContent>
          <TextField
            margin="dense"
            label="Nombre de usuario"
            fullWidth
            value={formValues.username}
            onChange={(e) => setFormValues({ ...formValues, username: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Email"
            fullWidth
            value={formValues.email}
            onChange={(e) => setFormValues({ ...formValues, email: e.target.value })}
          />
          <TextField
            margin="dense"
            label="Contraseña"
            type="password"
            fullWidth
            value={formValues.password}
            onChange={(e) => setFormValues({ ...formValues, password: e.target.value })}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} color="secondary">
            Cancelar
          </Button>
          <Button onClick={handleFormSubmit} color="primary">
            Guardar
          </Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default UserManagementPage;
