// src/components/LearningStyleFelderSilvermanForm.tsx
import React, { useState } from 'react';
import axios from 'axios';
import { classifyLearningStyleFelderSilverman } from '../utils/learningStyleClassifierFelderSilveraman';
import { Box, Button, Container, Typography, Alert, Grid, LinearProgress, FormControl, FormLabel, RadioGroup, FormControlLabel, Radio, Card, CardContent, Accordion, AccordionSummary, AccordionDetails, Modal } from '@mui/material';
import ExpandMoreIcon from '@mui/icons-material/ExpandMore';
import { questions } from '../components/Data/questions';

const QUESTIONS_PER_PAGE = 5;

const LearningStyleFelderSilvermanForm: React.FC = () => {
  const [responses, setResponses] = useState<number[]>(Array(questions.length).fill(0));
  const [learningStyle, setLearningStyle] = useState<string[]>([]);
  const [error, setError] = useState<string | null>(null);
  const [currentPage, setCurrentPage] = useState<number>(0);
  const [helpOpen, setHelpOpen] = useState<boolean>(true);

  const handleChange = (index: number, value: number) => {
    const newResponses = [...responses];
    newResponses[index] = value;
    setResponses(newResponses);
  };

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (responses.includes(0)) {
      setError('Por favor responde todas las preguntas antes de enviar.');
      return;
    }

    try {
      const calculatedLearningStyle = classifyLearningStyleFelderSilverman(responses);
      setLearningStyle(calculatedLearningStyle);

      const studentId = localStorage.getItem('studentId');
      if (!studentId) {
        setError('Falta el ID del estudiante');
        return;
      }

      await axios.put('http://localhost:5000/api/students/updateLearningStyle', {
        studentId,
        responses,
      });

      setError(null);
    } catch (error) {
      setError('Error al enviar el formulario');
    }
  };

  const handleNextPage = () => {
    setCurrentPage(currentPage + 1);
  };

  const handlePreviousPage = () => {
    setCurrentPage(currentPage - 1);
  };

  const renderQuestions = () => {
    const start = currentPage * QUESTIONS_PER_PAGE;
    const end = start + QUESTIONS_PER_PAGE;
    return questions.slice(start, end).map((question, index) => (
      <Card key={index} sx={{ marginBottom: 2 }}>
        <CardContent>
          <Accordion>
            <AccordionSummary expandIcon={<ExpandMoreIcon />}>
              <Typography variant="body1">{`${start + index + 1}. ${question.question}`}</Typography>
            </AccordionSummary>
            <AccordionDetails>
              {question.helpText && (
                <Accordion>
                  <AccordionSummary expandIcon={<ExpandMoreIcon />}>
                    <Typography variant="body2" color="textSecondary" gutterBottom>
                      Ayuda
                    </Typography>
                  </AccordionSummary>
                  <AccordionDetails>
                    <Typography variant="body2" color="textSecondary" gutterBottom>
                      {question.helpText}
                    </Typography>
                  </AccordionDetails>
                </Accordion>
              )}
              <FormControl component="fieldset">
                <FormLabel component="legend">Selecciona una opción:</FormLabel>
                <RadioGroup
                  aria-label={`question-${start + index}`}
                  name={`question-${start + index}`}
                  value={responses[start + index]}
                  onChange={(e) => handleChange(start + index, parseInt(e.target.value))}
                  sx={{ '& .MuiFormControlLabel-root': { justifyContent: 'flex-start' } }}
                >
                  {question.options.map((option, idx) => (
                    <FormControlLabel
                      key={idx}
                      value={idx + 1}
                      control={<Radio />}
                      label={`${String.fromCharCode(65 + idx)}. ${option}`}
                      sx={{ justifyContent: 'flex-start' }}
                    />
                  ))}
                </RadioGroup>
              </FormControl>
            </AccordionDetails>
          </Accordion>
        </CardContent>
      </Card>
    ));
  };

  const totalPages = Math.ceil(questions.length / QUESTIONS_PER_PAGE);
  const progress = (currentPage + 1) / totalPages * 100;

  return (
    <Container maxWidth="md">
      <Modal open={helpOpen} onClose={() => setHelpOpen(false)}>
        <Box sx={{ position: 'absolute', top: '50%', left: '50%', transform: 'translate(-50%, -50%)', width: 400, bgcolor: 'background.paper', border: '2px solid #000', boxShadow: 24, p: 4 }}>
          <Typography variant="h6" component="h2">
            Ayuda para diligenciamiento
          </Typography>
          <Typography sx={{ mt: 2 }}>
            Bienvenido al Inventario de Estilos de Aprendizaje de Felder Silverman. Por favor, responde todas las preguntas según tu preferencia. Esta información nos ayudará a entender mejor tu estilo de aprendizaje.
          </Typography>
          <Button onClick={() => setHelpOpen(false)} sx={{ mt: 2 }} variant="contained" color="primary">
            Entendido
          </Button>
        </Box>
      </Modal>
      <Box component="form" onSubmit={handleSubmit} sx={{ mt: 4 }}>
        <Typography variant="h4" component="h1" gutterBottom>
          Cuestionario de Estilos de Aprendizaje
        </Typography>
        {error && <Alert severity="error">{error}</Alert>}
        {renderQuestions()}
        <Grid container spacing={2} justifyContent="space-between">
          <Grid item>
            <Button
              type="button"
              variant="contained"
              color="primary"
              onClick={handlePreviousPage}
              disabled={currentPage === 0}
            >
              Anterior
            </Button>
          </Grid>
          <Grid item>
            <Button
              type="button"
              variant="contained"
              color="primary"
              onClick={handleNextPage}
              disabled={currentPage === totalPages - 1}
            >
              Siguiente
            </Button>
          </Grid>
        </Grid>
        {currentPage === totalPages - 1 && (
          <Box textAlign="center" mt={2}>
            <Button type="submit" variant="contained" color="primary">
              Enviar
            </Button>
          </Box>
        )}
        <Box mt={2}>
          <LinearProgress variant="determinate" value={progress} />
          <Typography variant="body2" color="textSecondary" align="center">
            {Math.round(progress)}% Completado
          </Typography>
        </Box>
      </Box>
    </Container>
  );
};

export default LearningStyleFelderSilvermanForm;
