// src/pages/admin/UserManagementPage.tsx

import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { User } from '../../models/User';
import UserList from '../../components/UserList';
import {
  Container,
  Button,
  CircularProgress,
  Dialog,
  DialogActions,
  DialogContent,
  DialogTitle,
  TextField,
  Typography,
  Pagination,
} from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import VisibilityIcon from '@mui/icons-material/Visibility';

const UserManagementPage: React.FC = () => {
  const [users, setUsers] = useState<User[]>([]);
  const [selectedUser, setSelectedUser] = useState<User | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [loading, setLoading] = useState<boolean>(false);
  const [openDialog, setOpenDialog] = useState<boolean>(false);
  const [currentPage, setCurrentPage] = useState<number>(1);
  const [usersPerPage] = useState<number>(5);
  const [isEditing, setIsEditing] = useState<boolean>(false); // Estado para determinar si estamos editando

  useEffect(() => {
    fetchUsers();
  }, [currentPage]);

  const fetchUsers = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Token no encontrado');
      }
      const response = await axios.get('http://localhost:5000/api/users', {
        headers: { Authorization: `Bearer ${token}` },
      });
      const sortedUsers = response.data.sort(
        (a: User, b: User) => new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
      );
      setUsers(sortedUsers);
      setError(null);
    } catch (error: any) {
      setError('Error al obtener usuarios: ' + (error.message || error));
    } finally {
      setLoading(false);
    }
  };

  const handleDeleteUser = async (userId: string) => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Token no encontrado');
      }
      await axios.delete(`http://localhost:5000/api/users/${userId}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setUsers(users.filter((user) => user._id !== userId));
      setError(null);
    } catch (error: any) {
      setError('Error al eliminar usuario: ' + (error.message || error));
    }
  };

  const handleOpenDialog = (user?: User | null, editMode = false) => {
    setSelectedUser(user || null);
    setIsEditing(editMode); // Establecer el modo de edición según la acción
    setOpenDialog(true);
  };

  const handleCloseDialog = () => {
    setOpenDialog(false);
    setSelectedUser(null);
    setIsEditing(false);
  };

  const handleSaveUser = async () => {
    if (!selectedUser) return;

    try {
      const token = localStorage.getItem('token');
      if (!token) {
        throw new Error('Token no encontrado');
      }
      await axios.put(
        `http://localhost:5000/api/users/${selectedUser._id}`,
        selectedUser,
        {
          headers: { Authorization: `Bearer ${token}` },
        }
      );
      fetchUsers(); // Actualizar la lista de usuarios
      handleCloseDialog();
    } catch (error: any) {
      setError('Error al actualizar usuario: ' + (error.message || error));
    }
  };

  const handlePageChange = (event: React.ChangeEvent<unknown>, page: number) => {
    setCurrentPage(page);
  };

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    if (selectedUser) {
      setSelectedUser({ ...selectedUser, [e.target.name]: e.target.value });
    }
  };

  const paginatedUsers = users.slice(
    (currentPage - 1) * usersPerPage,
    currentPage * usersPerPage
  );

  return (
    <Container maxWidth="lg" style={{ marginTop: 20 }}>
      <Typography variant="h3" gutterBottom>
        Administración de Usuarios
      </Typography>
      {error && <Typography color="error">{error}</Typography>}
      {loading ? (
        <CircularProgress />
      ) : (
        <>
          <UserList
            users={paginatedUsers}
            onDelete={handleDeleteUser}
            onEdit={(user) => handleOpenDialog(user, true)} // Modo de edición
            onView={(user) => handleOpenDialog(user, true)} // Modo de visualización y edición
          />
          <Pagination
            count={Math.ceil(users.length / usersPerPage)}
            page={currentPage}
            onChange={handlePageChange}
            color="primary"
            sx={{ mt: 2 }}
          />
        </>
      )}
      <Dialog open={openDialog} onClose={handleCloseDialog}>
        <DialogTitle>{isEditing ? 'Editar Usuario' : 'Ver Perfil del Usuario'}</DialogTitle>
        <DialogContent>
          {selectedUser && (
            <>
              <TextField
                margin="dense"
                label="Nombre de Usuario"
                type="text"
                fullWidth
                name="username"
                value={selectedUser.username}
                onChange={handleInputChange}
                InputProps={{
                  readOnly: !isEditing,
                }}
              />
              <TextField
                margin="dense"
                label="Email"
                type="email"
                fullWidth
                name="email"
                value={selectedUser.email}
                onChange={handleInputChange}
                InputProps={{
                  readOnly: !isEditing,
                }}
              />
              <TextField
                margin="dense"
                label="Fecha de Creación"
                type="text"
                fullWidth
                value={new Date(selectedUser.createdAt).toLocaleDateString()}
                InputProps={{
                  readOnly: true,
                }}
              />
            </>
          )}
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloseDialog} color="primary">
            {isEditing ? 'Cancelar' : 'Cerrar'}
          </Button>
          {isEditing && (
            <Button onClick={handleSaveUser} color="primary" variant="contained">
              Guardar
            </Button>
          )}
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default UserManagementPage;
