import React, { useState, useEffect } from 'react';
import { useSearchParams } from 'react-router-dom';
import { SearchResult, search } from '../../services/searchService';
import { Container, Typography, List, ListItem, ListItemText, Button } from '@mui/material';

const SearchResultsPage: React.FC = () => {
  const [searchParams] = useSearchParams();
  const query = searchParams.get('q') || '';
  const [results, setResults] = useState<SearchResult[]>([]);
  const [showResults, setShowResults] = useState(true); // Estado para el toggle

  useEffect(() => {
    const fetchResults = async () => {
      try {
        const searchResults = await search(query);
        setResults(searchResults);
      } catch (error) {
        console.error('Error fetching search results:', error);
      }
    };

    if (query) {
      fetchResults();
    }
  }, [query]);

  const handleToggle = () => {
    setShowResults(!showResults); // Alternar el estado de visibilidad
  };

  return (
    <Container maxWidth="lg" style={{ marginTop: 20 }}>
      <Typography variant="h4" gutterBottom>
        Resultados de b√∫squeda para "{query}"
      </Typography>
      <Button variant="contained" color="primary" onClick={handleToggle}>
        {showResults ? 'Ocultar Resultados' : 'Mostrar Resultados'}
      </Button>
      {showResults && (
        <List>
          {results.map((result) => (
            <ListItem key={result._id}>
              <ListItemText primary={result.title} secondary={result.description} />
            </ListItem>
          ))}
        </List>
      )}
    </Container>
  );
};

export default SearchResultsPage;
